import React, { useState } from 'react';
import './App.css';

const generatePassword = (length, charset) => {
  let password = '';
  for (let i = 0; i < length; i++) {
    const randomIndex = Math.floor(Math.random() * charset.length);
    password += charset[randomIndex];
  }
  return password;
};

function App() {
  const [length, setLength] = useState(12);
  const [charset, setCharset] = useState('letters');
  const [password, setPassword] = useState('');

  const handleGeneratePassword = () => {
    let characters = '';
    if (charset === 'letters') {
      characters = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
    } else if (charset === 'numbers') {
      characters = '0123456789';
    } else if (charset === 'both') {
      characters = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    }

    const newPassword = generatePassword(length, characters);
    setPassword(newPassword);
  };

  const handleLengthChange = (e) => {
    setLength(parseInt(e.target.value));
  };

  const handleCharsetChange = (e) => {
    setCharset(e.target.value);
  };

  return (
    <div className="App">
      <div className="card">
        <h1>Hoşgeldiniz, Şifre Oluşturucusu</h1>
        <div>
          <label>Şifre Uzunluğu:
            <input
              type="number"
              value={length}
              onChange={handleLengthChange}
              min="4"
              max="20"
            />
          </label>
        </div>
        <div>
          <input
            type="radio"
            id="letters"
            name="charset"
            value="letters"
            checked={charset === 'letters'}
            onChange={handleCharsetChange}
          />
          <label htmlFor="letters">Alfabe</label>
          <input
            type="radio"
            id="numbers"
            name="charset"
            value="numbers"
            checked={charset === 'numbers'}
            onChange={handleCharsetChange}
          />
          <label htmlFor="numbers">Sayılar</label>
          <input
            type="radio"
            id="both"
            name="charset"
            value="both"
            checked={charset === 'both'}
            onChange={handleCharsetChange}
          />
          <label htmlFor="both">Her ikisi</label>
        </div>
        {password ? (
          <div>
            <input type="text" value={password} readOnly />
            <button onClick={() => setPassword('')}>Tekrar Oluştur</button>
          </div>
        ) : (
          <button onClick={handleGeneratePassword}>Oluştur</button>
        )}
      </div>
    </div>
  );
}

export default App;
